authFunction:
function authFunction(uname, password, $http, $rootScope, success, error) {

    try {
        var authSuccess = success;
        var authError = error;
        $rootScope.middlewareServerUrl = "https://my.massasoit.mass.edu/bannermobile";
        $rootScope.msgUrl = "https://my.massasoit.mass.edu";
        //Banner Authentication with LDAP - middlware auth

        var url = "https://my.massasoit.mass.edu/bannermobile/services/authenticate/login";
        var casurl="https://cas.massasoit.mass.edu/cas/v1/tickets";
        var data = {};
        var casdata = {};
        if (window.device) {
            data = "username=" + uname + "&password=" + password;
            casdata = "server="+casurl+"&username=" + uname + "&password=" + password;
        } else {
            url = "/websimulator/json?url=" + url;
            data = {
                method: "POST",
                body: "username=" + uname + "&password=" + password
            };
            casdata = {
                method: "POST",
                body: "server="+casurl+"&username=" + uname + "&password=" + password
            };
        }
        var authUrl = url;
        var authData = data;
        $rootScope.generateST = function(tgt, service, callback) {
            //Bypassing the ST as there is no service ticket required here..
            callback(tgt);
        };

        $rootScope.generateMsgST = function(tgt, service, callback) {
         var url_server = casurl+'/'+tgt;
        $.ajax({
                         url:url_server,
                         type:"POST",
                         async:true,
                         cache:false,
                         data:{service:service},                         
                         success:function(data)
                         {
                         callback(data);
                         },
                         error:function(response)
                         {
                         console.log("D: Inside Error of generateST");
                         $.unblockUI();
                         }
                         });

            
        };

        $rootScope.postAPI = function(endpoint, postdata, callback) {
            $.blockUI();
            $rootScope.generateST($rootScope.ticket, endpoint, function(st) {
                var serviceurl = endpoint + "?ticket=" + st;
                var url = "";
                var proxyMethod = "POST";
                var proxyData = postdata;
                if (window.device) {
                    url = serviceurl;
                } else {
                    url = "/websimulator/json?url=" + serviceurl;
                    proxyMethod = "POST";
                    proxyData = {
                        method: "POST",
                        body: postdata
                    }
                }
                $http({
                    method: proxyMethod,
                    url: url,
                    data: proxyData
                }).
                success(function(data, status, headers, config) {
                    $.unblockUI();
                    callback(data, status, headers, config);
                }).
                error(function(data, status, headers, config) {
                    $.unblockUI();
                    //alert("Error in service call : " + data + " Status : " + status);
                    if (status == 403) { //Ticket expired.. need to relogin
                        $http.post(authUrl, authData).success(function(data, status, headers, config) {

                            if (!data.error) {
                                $rootScope.ticket = data.ticket;
                                $rootScope.getAPI($rootScope.middlewareServerUrl + "/services/student/banneruserroles", function(roles) {
                                    if (roles && roles.getuserroles && roles.getuserroles[0]) {
                                        var uroles = [];
                                        if (roles.getuserroles[0].STUDENT == "Y") {
                                            uroles.push("STUDENT");
                                        }
                                        if (roles.getuserroles[0].EMPLOYEE == "Y") {
                                            uroles.push("EMPLOYEE");
                                        }
                                        if (roles.getuserroles[0].FACULTY == "Y") {
                                            uroles.push("FACULTY");
                                        }
                                        if (roles.getuserroles[0].GOVROLE_ALUMNI_IND == "Y") {
                                            uroles.push("ALUMNI");
                                        }
                                        if (roles.getuserroles[0].GOVROLE_FINANCE_IND == "Y") {
                                            uroles.push("FINANCE");
                                        }
                                        if (roles.getuserroles[0].FINAID == "Y") {
                                            uroles.push("FINAID");
                                        }
                                        data.roles = uroles;
                                    }
                                    console.log(data);
                                    authSuccess(data);
                                });

                            } else {
                                authSuccess(data);
                            }
                            //authSuccess(data);
                            $rootScope.postAPI(endpoint, postdata, callback);
                        }).error(function(data, status, headers, config) {
                            authError(data);

                        });
                    }

                });

            });

        };

        $rootScope.getAPI = function(endpoint, callback) {
            $.blockUI();
            $rootScope.generateST($rootScope.ticket, endpoint, function(st) {
                var serviceurl = endpoint + "?ticket=" + st;
                var url = "";
                var proxyMethod = "GET";
                var proxyData = {};
                if (window.device) {
                    url = serviceurl;
                } else {
                    url = "/websimulator/json?url=" + encodeURIComponent(serviceurl);
                    proxyMethod = "POST";
                    proxyData = {
                        method: "GET"
                    }
                }
                $http({
                    method: proxyMethod,
                    url: url,
                    data: proxyData
                }).
                success(function(data, status, headers, config) {
                    $.unblockUI();
                    callback(data, status, headers, config);
                }).
                error(function(data, status, headers, config) {
                    $.unblockUI();
                    //alert("Error in service call : " + data + " Status : " + status);
                    if (status == 403) { //Ticket expired.. need to relogin
                        $http.post(authUrl, authData).success(function(data, status, headers, config) {

                            if (!data.error) {
                                $rootScope.ticket = data.ticket;
                                $rootScope.getAPI($rootScope.middlewareServerUrl + "/services/student/banneruserroles", function(roles) {
                                    if (roles && roles.getuserroles && roles.getuserroles[0]) {
                                        var uroles = [];
                                        if (roles.getuserroles[0].GOVROLE_STUDENT_IND == "Y") {
                                            uroles.push("STUDENT");
                                        }
                                        if (roles.getuserroles[0].GOVROLE_EMPLOYEE_IND == "Y") {
                                            uroles.push("EMPLOYEE");
                                        }
                                        if (roles.getuserroles[0].GOVROLE_FACULTY_IND == "Y") {
                                            uroles.push("FACULTY");
                                        }
                                        if (roles.getuserroles[0].GOVROLE_ALUMNI_IND == "Y") {
                                            uroles.push("ALUMNI");
                                        }
                                        if (roles.getuserroles[0].GOVROLE_FINANCE_IND == "Y") {
                                            uroles.push("FINANCE");
                                        }
                                        if (roles.getuserroles[0].GOVROLE_FINAID_IND == "Y") {
                                            uroles.push("FINAID");
                                        }
                                        data.roles = uroles;
                                    }
                                    console.log(data);
                                    authSuccess(data);
                                });

                            } else {
                                authSuccess(data);
