    var baseUrl = "https://cas.massasoit.mass.edu";
    var appURL = "https://my.massasoit.mass.edu";
    var url = baseUrl + "/cas/v1/tickets/";
    $rootScope.generateCASST = function(tgt, service, callback) {
        var url = baseUrl + "/cas/v1/tickets/";
        var data = {};
        if (window.device) {
            data = "service=" + service;
        } else {
            url = "/websimulator/text?url=" + baseUrl + "/cas/v1/tickets/";
            data = {
                method: "POST",
                body: "service=" + service
            };
        }
        $.ajax({
            url: url + tgt,
            type: "POST",
            async: true,
            data: data,
            error: function(response) {
                $rootScope.generateTGT(function(tgt1) {
                    //Relay back the same API call
                    $rootScope.generateCASST(tgt1, service, callback);
                });
            },
            success: function(data) {
                callback(data);
            }
        });

    };

    $rootScope.postAPI2 = function(endpoint, postdata, callback) {
        if (!$rootScope.tgtticket) {
            $rootScope.generateTGT(function(tgt1) {
                //Relay back the same API call
                $rootScope.postAPI2(endpoint, postdata, callback);
            });
        } else {
            $.blockUI();
            $rootScope.generateCASST($rootScope.tgtticket, endpoint, function(st) {
                var serviceurl = endpoint + "?ticket=" + st;
                var url = "";
                var proxyMethod = "POST";
                var proxyData = postdata;
                if (window.device) {
                    url = serviceurl;
                } else {
                    url = "/websimulator/json?url=" + serviceurl;
                    proxyMethod = "POST";
                    proxyData = {
                        method: "POST",
                        body: postdata
                    }
                }
                if (window.device) {
                    $.ajax({
                        url: url,
                        type: "POST",
                        async: true,
                        data: postdata,
                        error: function(response) {
                            //alert("Error : " + response);
                            $.unblockUI();

                        },
                        success: function(data) {
                            $.unblockUI();
                            callback(data);
                        }
                    });
                } else {
                    $http({
                        method: proxyMethod,
                        url: url,
                        data: proxyData
                    }).
                    success(function(data, status, headers, config) {
                        $.unblockUI();
                        callback(data, status, headers, config);
                    }).
                    error(function(data, status, headers, config) {
                        $.unblockUI();

                    });
                }
            });
        }
    };

    $rootScope.getAPI2 = function(endpoint, callback) {
        if (!$rootScope.tgtticket) {
            $rootScope.generateTGT(function(tgt1) {
                //Relay back the same API call
                $rootScope.getAPI2(endpoint, callback);
            });
        } else {
            $.blockUI();
            $rootScope.generateCASST($rootScope.tgtticket, endpoint.replace("$$", ""), function(st) {
                var serviceurl = endpoint.indexOf("$$") != -1 ? (endpoint.replace("$$", "?ticket=" + st + "")) : (endpoint + "?ticket=" + st);
                var url = "";
                var proxyMethod = "GET";
                var proxyData = {};
                if (window.device) {
                    url = serviceurl;
                } else {
                    url = "/websimulator/json?url=" + encodeURIComponent(serviceurl);
                    //url = "/websimulator/json?url=" + serviceurl;
                    proxyMethod = "POST";
                    proxyData = {
                        method: "GET"
                    }
                }
                $http({
                    method: proxyMethod,
                    url: url,
                    data: proxyData
                }).
                success(function(data, status, headers, config) {
                    $.unblockUI();
                    callback(data, status, headers, config);
                }).
                error(function(data, status, headers, config) {
                    $.unblockUI();
                });
            });
        }
    };

    $rootScope.generateTGT = function(callback) {
        $.blockUI();
        var uname = $.jStorage.get("username");
        var password = $.jStorage.get("password");
        var data = {};
        if (window.device) {
            data = "username=" + uname + "&password=" + password;
        } else {
            url = "/websimulator/text?url=" + url;
            data = {
                method: "POST",
                body: "username=" + uname + "&password=" + password
            };
        }

        $.ajax({
            url: url,
            type: "POST",
            async: true,
            data: data,
            error: function(response) {
                $.unblockUI();
            },
            success: function(data) {
                $.unblockUI();
            },
            complete: function(response, textStatus) {
                $.unblockUI();
                if (textStatus == "success" && response.status == 201) {
                    var str = response.getResponseHeader('Location');
                    var tgtTicket = str.substr(str.lastIndexOf('/') + 1);
                    $rootScope.tgtticket = tgtTicket;
                    callback(tgtTicket);
                } else {
                    alert("Oops! Error occurred while processing this request.");
                }
            }
        });
    }

    $scope.loadData = function() {
        var pn = $scope.curpageNum + 1;
        $rootScope.getAPI2(appURL + "/Alerts/AlertsService/AlertsInfoService/inbox" + "$$&pageNum=" + pn + "&pageSize=" + $scope.curpageSize, function(data) {
            if (data.page.length == 0) {
                var gotoHome = function() {
                    $rootScope.$apply(function() {
                        $location.path("/home");
                    });
                };
                if (window.device) {
                    navigator.notification.alert("No messages found.",
                        gotoHome, "Message Center", 'Ok');

                } else {
                    apprise("No messages found.", {
                        'verify': false,
                        'textYes': "Ok"
                    }, function(r) {
                        gotoHome();
                    });
                }
            } else {
                $scope.alerts = $scope.alerts.concat(data.page);
                $rootScope.alerts = $scope.alerts;
                $scope.curpageNum = data.pageNum;
                $scope.curpageSize = data.pageSize;
                $scope.totalResults = data.totalResults;
                console.log(data);
            }
        });
    }
    $scope.loadData();
}

