function pageprocessorTwitterLauncher(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    if (window.device) {
        var successCallback = function() {
            $location.path("/home");
        };
        if (window.device.platform == "Android") {
            var errorCallback = function() {
                $scope.loadurl(pagedef.playstoreurl);
            };
            window.plugins.launcher.launch({
               // packageName: pagedef.androidpackage
            //}, successCallback, errorCallback);
              uri: pagedef.iosuri
            }, successCallback, errorCallback);
              
        } else if (window.device.platform == "iOS") {
            var errorCallback = function() {
                $scope.loadurl(pagedef.itunesurl);
            };
            window.plugins.launcher.launch({
                uri: pagedef.iosuri
            }, successCallback, errorCallback);
        }
    } else {
        apprise("This feature is not available on Emulator", {
            "verify": false,
            "textYes": "Ok"
        }, function(r) {});
    }
    $scope.loadurl = function(url) {
        if (window.device) {
            $.blockUI();
        }
        var iabRef = window.open(url, "_blank", "location=no,hidden=yes");
        var hideBlockUi = function() {
            iabRef.show();
            $.unblockUI();
        };
        var iabClose = function(data) {
            window.location.href = "index.html#home";
        };
        iabRef.addEventListener("exit", iabClose);
        var loadStop = function(data) {
            hideBlockUi();
        };
        iabRef.addEventListener("loadstop", loadStop);
    }
}

function pageprocessorClassCancel(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);
    $scope.cancel = function(url) {
        //$rootScope.cancelurl = url;
        $.blockUI();

        var feed = new google.feeds.Feed(url);
        feed.setNumEntries(-1);
        feed.setResultFormat(google.feeds.Feed.JSON);
        feed.load(function(result) {

            if (!result.error) {
                $scope.feeds = result.feed.entries;
                $rootScope.feeds = result.feed.entries;
                // if (JSON.stringify($scope.feeds) == "[]") navigator.notification.alert("Not available now");

                $location.path("/app/ClassCancel/ClassCancelPage2");
                $scope.$apply();
                $.unblockUI();
            }
        });

    }



}

function pageprocessorClassCancelPage2(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {

$scope.feed =  $rootScope.feeds;


var data1 = $compile($(pagedef.datatemplate))($scope);$("#appContent").append(data1);}

function pageprocessorClassCancelPage3(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
$scope.feed = $rootScope.feeds[$routeParams.index];
    $scope.openurl = function() {
        window.open($scope.feed.link, "_blank", "location=no");
    };
    var template = pagedef.datatemplate;
    var data1 = $compile($(template))($scope);
    $("#appContent").append(data1);
    $scope.trustHtml = function(dt) {
        return $sce.trustAsHtml(dt);
    }
}
